import bisect

def max_teleporters_used(n, c, a):
    total = 1
    start = []
    for i in range(n):
        start.append(a[i] + (i + 1))
        a[i] += min(i + 1, n - i)
    sorted_arr = sorted(a)
    pref = [sorted_arr[0]]
    for i in range(1, n):
        pref.append(pref[-1] + sorted_arr[i])
    for i in range(n):
        if c - start[i] >= 0:
            index = bisect.bisect_left(pref, c - start[i])
            if index >= n:
                index -= 1
            if pref[index] > c - start[i]:
                index -= 1
            if index == -1:
                total = max(total, 1)
                continue
            if sorted_arr[index] >= a[i]:
                index = bisect.bisect_left(pref, c - (start[i] - a[i]))
                if index >= n:
                    index -= 1
                if pref[index] > c - (start[i] - a[i]):
                    index -= 1
                cur = index + 1
            else:
                cur = index + 2
            total = max(total, cur)
    return total

