def find_square_tables(x):
    i = 1
    t = 0
    r = []
    while True:
        i += 1
        x -= i * i
        if x < 0:
            break
        t += i
        m = x // t
        if m * t != x:
            continue
        r.append((i, m + i))
        if m > 0:
            r.append((m + i, i))
    r.sort()
    return (len(r), r)

