from collections import defaultdict

def find_blog_writing_order(n, m, references, desired_topics):
    adj = defaultdict(list)
    for (u, v) in references:
        adj[u].append(v)
        adj[v].append(u)
    t = [(index + 1, value) for (index, value) in enumerate(desired_topics)]
    t = sorted(t, key=lambda tup: tup[1])
    written_topics_for_blog = {}
    result = []
    impossible = True
    for (blog, topic) in t:
        neighbours_topics = set([])
        for neighbour in adj[blog]:
            if neighbour in written_topics_for_blog:
                neighbours_topics.add(written_topics_for_blog[neighbour])
        minimum_topic = 1
        while minimum_topic in neighbours_topics:
            minimum_topic += 1
        if topic in neighbours_topics or topic != minimum_topic:
            impossible = True
            return -1
        written_topics_for_blog[blog] = topic
        result.append(blog)
    return result

