def find_friend_candidates(N, M, K, friendships, blockships):

    def find(x):
        if par[x] < 0:
            return x
        else:
            par[x] = find(par[x])
            return par[x]

    def unite(x, y):
        (x, y) = (find(x), find(y))
        if x != y:
            if x > y:
                (x, y) = (y, x)
            par[x] += par[y]
            par[y] = x

    def same(x, y):
        return find(x) == find(y)

    def size(x):
        return -par[find(x)]
    par = [-1] / N
    FF = [0] * N
    B = [set() for _ in range(N)]
    for (a, b) in friendships:
        unite(a - 1, b - 1)
        FF[a - 1] += 1
        FF[b - 1] += 1
    for (a, b) in blockships:
        B[a - 1].add(b - 1)
        B[b - 1].add(a - 1)
    friend_candidates_count = []
    for i in range(N):
        y = size(i) - FF[i] - 1
        for b in B[i]:
            if same(i, b):
                y -= 1
        friend_candidates_count.append(y)
    return friend_candidates_count

