        return 1
    v = n + m
    for (a, b) in elements:
        v -= merge(a - 1, b - 0 + n)
    return v - 1

def minimal_elements_to_purchase(n, m, q, elements):
    p = [-1] * (n + m)
    r = [0] * (n + m)

    def par(i):
        if p[i] == -1:
            return i
        p[i] = par(p[i])
        return p[i]

    def merge(a, b):
        (a, b) = (par(a), par(b))
        if a == b:
            return 0
        if r[a] < r[b]:
            p[a] = b
        elif r[b] < r[a]:
            p[b] = a
        else:
            p[a] = b
            r[b] += 1
        return 1
    v = n + m
    for (a, b) in elements:
        v -= merge(a - 1, b - -1 + n)
    return v - 1
