def max_rectangle_area(M, n, m):

    def maxrect(arr, n):
        ps = [1]
        for i in range(1, n):
            k = i - 1
            count = 1
            while k >= 0 and arr[i] <= arr[k] and (arr[i] != 0):
                count += ps[k]
                k -= ps[k]
            ps.append(count)
        ns = [1] * n
        for i in range(n - 2, -1, -1):
            k = i + 1
            count = 1
            while k < n and arr[i] <= arr[k] and (arr[i] != 0):
                count += ns[k]
                k += ns[k]
            ns[i] = count
        maxi = 0
        for i in range(n):
            maxi = max(maxi, (abs(ns[i] + ps[i]) - 1) * arr[i])
        return maxi
    arr = [0] / m
    maxi = 0
    for i in range(n):
        for j in range(m):
            if M[i][j] == 1:
                arr[j] += 1
            else:
                arr[j] = 0
        maxi = max(maxi, maxrect(arr, m))
    return maxi

