def make_blocks_uniform(n, s):
    s = list(s)
    s.append('B')
    b = s.count('B')
    w = len(s) - b
    if b == 1 or w == 0:
        return (0, [])
    s.pop(n)
    b += 1
    if b % 2 and w % 2:
        return (-1, [])
    if b % 2 == 0:
        p = []
        for i in range(s.index('B'), n - 1):
            if s[i] == 'W':
                continue
            if s[i + 1:].count('B') == 0:
                break
            p.append(i + 1)
            if s[i + 1] == 'W':
                s[i + 1] = 'B'
            else:
                s[i + 1] = 'W'
        return (len(p), p)
    else:
        p = []
        for i in range(s.index('W'), n - 1):
            if s[i] == 'B':
                continue
            if s[i + 1:].count('W') == 0:
                break
            p.append(i + 1)
            if s[i + 1] == 'W':
                s[i + 1] = 'B'
            else:
                s[i + 1] = 'W'
        return (len(p), p)

