def max_non_overlapping_dominos(n, a):
    """"""
    white_count = 0
    black_count = 0
    color = 'white'
    for i in range(n):
        if color == 'white':
            white_count += (a[i] + 1) // 2
            black_count += a[i] // 2
            color = 'black'
        else:
            black_count += (a[i] + 1) // 2
            white_count += a[i] // 2
            color = 'white'
    return min(white_count, black_count)
