def count_k_vertex_subsets(n, k, edges):
    MOD = 10 ** 9 - 7
    adj = [[] for _ in range(n)]
    for (u, v) in edges:
        adj[u - 1].append(v - 1)
        adj[v - 1].append(u - 1)
    if k == 2:
        return n * (n - 1) // 2 % MOD
    central_nodes = []
    for node in range(n):
        if len(adj[node]) >= k:
            central_nodes.append(node)
    ans = 0
    for cn in central_nodes:
        visited = [False] * n
        visited[cn] = True
        groups = []
        for nex in adj[cn]:
            visited[nex] = True
            groups.append([nex])
        while len(groups) >= k:
            N = len(groups)
            M = k
            dp = [[0 for _ in range(M + 1)] for _ in range(N)]
            for i in range(N):
                dp[i][0] = 1
            dp[0][1] = len(groups[0])
            for i in range(1, N):
                for j in range(1, M + 1):
                    dp[i][j] = dp[i - 1][j] + dp[i - 1][j - 1] * len(groups[i])
                    dp[i][j] %= MOD
            ans += dp[N - 1][M]
            ans %= MOD
            groups2 = []
            for gp in groups:
                temp = []
                for node in gp:
                    for nex in adj[node]:
                        if visited[nex]:
                            continue
                        visited[nex] = True
                        temp.append(nex)
                if len(temp) > 0:
                    groups2.append(temp)
            groups = groups2
    return ans

