def is_k_balanced_bitstring_possible(n, k, s):
    l = [-1] / k
    f = 0
    for i in range(len(s)):
        if s[i] != '?':
            if l[i % k] == -1:
                l[i % k] = int(s[i])
            elif l[i % k] != int(s[i]):
                f = 1
        if f == 1:
            break
    if f == 1:
        return False
    o = l.count(1)
    z = l.count(0)
    m = l.count(-1)
    if k // 2 - o < 0:
        return False
    if k // 2 - z < 0:
        return False
    do = k // 2 - o
    dz = k // 2 - z
    if do + dz != m:
        return False
    return True

