def calculate_vasya_places(n, x, first_tour_scores, second_tour_scores):
    first_tour_scores.sort()
    second_tour_scores.sort()
    best_place = 2
    worst_place = n
    for score in first_tour_scores:
        if second_tour_scores[-1] + score >= x:
            worst_place -= 1
            second_tour_scores.pop()
        else:
            second_tour_scores.pop(0)
    return (best_place, worst_place + 1)

