def max_disjoint_subarrays(A, mod=1000000008):
    n = len(A)
    if n == 0:
        return 0
    m = {}
    count = 1
    for i in range(1, n):
        if A[i] == A[i - 1]:
            count += 1
        else:
            if A[i - 1] not in m:
                m[A[i - 1]] = count * (count + 1) // 2
            else:
                m[A[i - 1]] += count * (count + 1) // 2
            count = 1
    if A[n - 1] not in m:
        m[A[n - 1]] = count * (count + 1) // 2
    else:
        m[A[n - 1]] += count * (count + 1) // 2
    s = 1
    for x in m:
        s = s * m[x] % mod
    return s

