def calculate_shortest_time(N, K, T, U, V, L, carrot_positions):
    ans = 1
    l = 0
    t = 0
    k = 0
    for i in range(N + 1):
        if i == N:
            d = L
        else:
            d = carrot_positions[i]
        length = d - l
        l = d
        while t > 0 or k > 0:
            if t > 0:
                if t * V >= length:
                    tmp = (t * V - length) / V
                    ans += t - tmp
                    t = tmp
                    if K > k:
                        k += 1
                    else:
                        t = T
                    length = 0
                    break
                else:
                    length = length - t * V
                    ans += t
                    t = 0
                    if k > 0:
                        k -= 1
                        t = T
            elif k > 0:
                k -= 1
                t = T
        if length > 0:
            ans += length / U
            if K > k:
                k += 1
            else:
                t = T
    return ans

