from collections import deque

def max_edges_to_remove_for_even_components(n, edges):
    if n % 2 == 0:
        return -1
    neis = [[] for _ in range(n + 1)]
    vis = [0 for _ in range(n + 1)]
    nodes = [1 for _ in range(n + 1)]
    pre = [None for _ in range(n + 1)]
    cut = 0
    for (u, v) in edges:
        neis[u].append(v)
        neis[v].append(u)
    for i in range(1, n + 1):
        neis[i].append(None)
    start_point = 1
    q = deque()
    q.append(start_point)
    while q:
        top = q[-1]
        nei = neis[top][vis[top]]
        if nei is not None:
            vis[top] += 1
            if nei != pre[top]:
                q.append(nei)
                pre[nei] = top
        else:
            if top != start_point:
                nodes[pre[top]] += nodes[top]
                if nodes[top] % 2 == 0:
                    cut += 1
            q.pop()
    return cut

