def maximize_bracket_beauty(n, bracket_string):

    def even(string):
        a = 0
        b = 0
        for c in string:
            if c == ')':
                a += 1
            if c == '(':
                b += 1
        return a == b

    def swap(arr, i, j):
        (arr[i], arr[j]) = (arr[j], arr[i])

    def calc(string):
        N = len(string)
        l = 0
        s = 0
        for char in string:
            if char == '(':
                s += 1
            else:
                s -= 1
            l = min(l, s)
        if s != 0:
            return 0
        t = 0
        for i in range(N):
            c = string[i]
            if c == '(':
                s += 1
                if s - l <= 1:
                    t += 1
            else:
                s -= 1
        return t
    string = list(bracket_string)
    if not even(string):
        return (0, (1, 1))
    highest = calc(string)
    a = b = 1
    for i in range(n):
        for j in range(i + 1, n):
            if string[i] == string[j]:
                continue
            swap(string, i, j)
            total = calc(string)
            if total > highest:
                highest = total
                (a, b) = (i, j)
            swap(string, i, j)
    return (highest, (a + 1, b + 1))

