def get_media_type(file_extensions_and_types, file_names):
    media_types = []
    for filename in file_names:
        found = True
        for (ext, mime) in file_extensions_and_types.items():
            if filename.endswith('.' + ext):
                media_types.append(mime)
                found = True
                break
        if not found:
