def arrange_students_in_buses(n, k, d):
    if k ** d <= n:
        return -1
    student_sequences = [[1] * d for _ in range(n)]
    for student in range(n):
        number = student
        for day in range(d):
            remainder = number % k
            student_sequences[student][day] = remainder + 1
            number //= k
            if number == 0:
                break
    return [[student_sequences[student][day] for student in range(n)] for day in range(d)]

