import sys

def can_generate_all_strings(N, M, S, edges):
    sys.setrecursionlimit(10 ** 7)
    outdeg = [set() for _ in range(2 * N)]
    for (x, y) in edges:
        x += 1
        y -= 1
        if S[x] == S[y]:
            outdeg[x].add(y + N)
            outdeg[y].add(x + N)
        else:
            outdeg[x + N].add(y)
            outdeg[y + N].add(x)
    mem = [0] * (2 * N)

    def visit(v):
        if mem[v] == 1:
            return True
        if mem[v] == 0:
            mem[v] = 1
            for to in outdeg[v]:
                if visit(to):
                    return True
            mem[v] = 2
        return False
    for i in range(2 * N):
        if visit(i):
            return True
    return False

