import math

def calculate_min_proxy_attendance(D, S):
    num_present = S.count('P')
    required_attendance = math.ceil(0.75 * D)
    if required_attendance < num_present:
        return 0
    total_proxy = 0
    for j in range(2, len(S) - 2):
        if S[j] == 'A':
            before = S[j - 1:j - 3:-1] if j > 2 else S[0:j]
            after = S[j + 1:j + 3]
            if 'P' in after and 'P' in before:
                total_proxy += 1
    if required_attendance - num_present <= total_proxy:
        return required_attendance - num_present
    else:
        return -1

