def is_prime(num):
    """Helper function to check if a number is prime."""
    if num < 2:
        return False
    k = int(num ** 0.5)
    for i in range(2, k + 1):
        if num % i == 0:
            return False
    return True

def max_unique_prime_factors(N):
    """"""
    p = 1
    c = 0
    i = 2
    while p <= N:
        if is_prime(i):
            p = p * i
            if p <= N:
                c += 1
        i += 1
    return c
