        else:
            edge = nxt
            sq += 1
            if past:
                sq += past.pop()
    past.append(sq)
    answer = 0
    for i in range(len(past)):
        if edge == 0:
            answer += past[-i - 1]
        edge = 1 - edge
    return answer

