import itertools

def remove_signs(s: str, signs: list) -> str:
    for sign in signs:
        s = s.replace(sign, '')
    return s

def get_permutations_of_strings(s1: str, s2: str, s3: str) -> list:
    return list(map(''.join, itertools.permutations([s1, s2, s3])))

def check_student_answers(initial_strings: list, student_answers: list) -> list:
    signs = ['', '_', '-']
    str1 = remove_signs(initial_strings[0], signs).lower()
    str2 = remove_signs(initial_strings[1], signs).lower()
    str3 = remove_signs(initial_strings[2], signs).lower()
    str_permutations = get_permutations_of_strings(str1, str2, str3)
    results = []
    for answer in student_answers:
        cleaned_answer = remove_signs(answer, signs).lower()
        if cleaned_answer in str_permutations:
            results.append('ACC')
        else:
            results.append('WA')
    return results

