def count_arithmetic_slices(A: list[int]) -> int:
    curr = 1
    total_slices = 0
    for i in range(2, len(A)):
        if A[i] - A[i - 1] == A[i - 1] - A[i - 2]:
            curr += 1
            total_slices += curr
        else:
            curr = 0
    return total_slices

