def get_binary_representation(N: int) -> str:
    binary_rep = ['0' for _ in range(30)]
    i = 1
    while N:
        if N & 1 == 1:
            binary_rep[30 - i - 1] = '1'
        N >>= 1
        i += 1
    return ''.join(binary_rep)

