def distribute_medals(n, p):
    if n < 10 and len(set(p)) < 3:
        return (0, 0, 0)
    C = []
    i = 0
    while i < n:
        j = i + 1
        while j < n and p[j] == p[i]:
            j += 1
        C.append(j - i)
        i = j
    half = n // 2
    g = C[0]
    s = 0
    b = 0
    i = 1
    while i < len(C) and g >= s:
        s += C[i]
        i += 1
    while i < len(C) and g >= b:
        b += C[i]
        i += 1
    if s == 0 or b == 0 or g >= s or (g >= b) or (g + s + b > half):
        return (0, 0, 0)
    while i < len(C):
        if g + s + b + C[i] > half:
            break
        b += C[i]
        i += 1
    return (g, s, b)

