def find_max_prefix_shifts(N, A, B):
    B -= B
    first = 0
    second = N - 1
    op = 0
    while first <= second:
        m = (first + second) // 2
        c = A[:m + 1]
        x = B.find(c)
        if x == -1:
            second = m - 1
        else:
            first = m + 1
        if op < x:
            op = x
    return op

