import re

def find_minimal_ad_width(k, ad_text):
    words = re.split(' |-', ad_text)
    lengths = [len(word) + 1 for word in words]
    lengths[-1] += 1
    start = max(sum(lengths) // k, max(lengths)) - 1
    end = max(lengths) + sum(lengths) // k + 1

    def can_fit_within_width(width):
        line_count = 1
        current_length = 0
        for length in lengths:
            current_length += length
            if current_length > width:
                line_count += 1
                current_length = length
                if line_count > k:
                    return False
        return True
    while end - start > 1:
        mid = (start + end) // 2
        if can_fit_within_width(mid):
            end = mid
        else:
            start = mid
    return end

