def process_tree_operations(n, nodes, queries):

    class BIT:

        def __init__(self, n):
            self.n = n
            self.data = [0] * (n + 1)

        def get_sum(self, i):
            ret = 0
            while i > 0:
                ret += self.data[i]
                i -= i & -i
            return ret

        def add(self, i, w):
            if i == 0:
                return
            while i <= self.n:
                self.data[i] += w
                i += i & -i

    def dfs(u, cnt):
        cnt -= 1
        l[u] = cnt
        for c in tree[u]:
            cnt = dfs(c, cnt)
        cnt += 1
        r[u] = cnt
        return cnt
    l = [0] * n
    r = [0] * n
    tree = [set(children) for (_, children) in nodes]
    bit = BIT(dfs(0, 1))
    results = []
    for query in queries:
        (query_type, v_or_u, w) = query
        if query_type == 1:
            results.append(bit.get_sum(r[v_or_u] - 1))
        else:
            bit.add(l[v_or_u], w)
            bit.add(r[v_or_u], -w)
    return results

