def count_shortest_paths(N, M, roads):
    g = [[0 for _ in range(N + 1)] for _ in range(N + 1)]
    for (Ai, Bi, Ci) in roads:
        g[Ai][Bi] = Ci
        g[Bi][Ai] = Ci
    x = []
    d = [-1] / (N + 1)
    d[1] = 0

    def snek(k, n, x, d, g):
        if k == n:
            x.append(d[n])
            return
        for i in range(1, n + 1):
            if g[k][i] != 0 and d[i] == -1:
                d[i] = d[k] + g[k][i]
                snek(i, n, x, d, g)
                d[i] = -1
    snek(1, N, x, d, g)
    x.sort()
    r = x[0]
    ans = 0
    for z in range(len(x)):
        if r == x[z]:
            ans += 1
    return ans

