    def __init__(self):
        self.parent = {}
        self.rank = {}

    def union(self, a, b):
        (a, b) = (self.find(a), self.find(b))
        if a == b:
            return
        if self.rank[a] == self.rank[b]:
            self.rank[a] += 1
        elif self.rank[b] > self.rank[a]:
            (a, b) = (b, a)
        self.parent[b] = a

    def find(self, x):
        if x not in self.parent:
            self.parent[x] = x
            self.rank[x] = 0
        if x != self.parent[x]:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

