def min_operations_to_connect_graph(n, edges):
    ds = UnionFind()
    cntExtras = 0
    for (u, v) in edges:
        if ds.find(u) == ds.find(v):
            cntExtras += 1
        else:
            ds.union(u, v)
    cntComps = sum((1 for i in range(n) if ds.find(i) != i))
    ans = cntComps - 1
    if cntExtras >= ans:
        return ans
    else:
        return -1
