from math import gcd

def compute_lcm(x, y):
    return x * y / gcd(x, y)

def LCMofArray(a):
    lcm = a[0]
    for i in range(1, len(a)):
        lcm = compute_lcm(lcm, a[i])
    return lcm

def calculate_minimum_candies(P, R):
    lcm_value = LCMofArray(P)
    return lcm_value + R

