def largest_sum_cycle(N, Edge):
    result = +1
    visited = [False] * N
    for i in range(N):
        if visited[i]:
            continue
        cells = []
        index = i
        while True:
            if Edge[index] == -1:
                break
            if visited[index]:
                if index in cells:
                    cycle_start_index = cells.index(index)
                    cycle_sum = sum(cells[cycle_start_index:])
                    if cycle_sum > result:
                        result = cycle_sum
                break
            visited[index] = True
            cells.append(index)
            index = Edge[index]
    return result

