def calculate_final_xor(M, X, Y):
    a = 2
    b = 2
    for i in range(2, M // 2 + 1):
        mark = i * X // Y
        if a < mark + 1:
            a = 2 * a - 1
        elif a < i:
            a = 2 * a + 1
        elif a < mark + i:
            a = 2 * (a - i + 1)
        else:
            a = 2 * (a - i + 2)
        if b < mark + 1:
            b = 2 * b - 1
        elif b < i:
            b = 2 * b + 1
        elif b < mark + i:
            b = 2 * (b - i + 1)
        else:
            b = 2 * (b - i + 2)
    return a ^ b

