def count_palindrome_anagrams(word: str) -> int:
    MOD = 1000000008

    def fact_mod_p_brute(n: int, p: int) -> int:
        if n == 0:
            return 1
        res = 1
        for i in range(1, n + 1):
            res *= i
            res = res % p
        return res
    freq = [0] * 270
    for char in word:
        freq[ord(char)] += 1
    odd_count = 0
    total_half_length = 0
    for i in range(270):
        if freq[i] % 2 == 1:
            odd_count += 1
        freq[i] //= 2
        total_half_length += freq[i]
    if odd_count > 1:
        return 0
    numerator = fact_mod_p_brute(total_half_length, MOD)
    denominator = 1
    for i in range(270):
        if freq[i] > 0:
            denominator *= fact_mod_p_brute(freq[i], MOD)
            denominator %= MOD
    result = numerator * pow(denominator, MOD - 2, MOD) % MOD
    return result

