def dynamic_connectivity(n, operations):
    par = [i for i in range(n)]

    def root(p):
        pp = p
        while par[pp] == pp:
            pp = par[pp]
        return pp
    results = []
    for op in operations:
        (command, p, q) = op
        if command == 'union':
            if root(p) != root(q):
                par[root(p)] = root(q)
        elif command == 'connected':
            results.append(root(p) == root(q))
    return results

