def calculate_beauty_sum(n: int, k: int, a: list) -> int:
    inf = 100000000000000001
    mod = 998244353
    A = [0] + sorted(a)
    ans = 0
    f = [[0] * (n + 10) for _ in range(k + 10)]
    for x in range(1, (A[n] - A[1]) // (k - 1) + 1):
        for i in range(1, n + 1):
            f[1][i] = 1
        for i in range(2, k + 1):
            sum = 0
            pre = 1
            for j in range(1, n + 1):
                while pre <= n and A[pre] + x <= A[j]:
                    sum += f[i - 1][pre]
                    sum %= mod
                    pre += 1
                f[i][j] = sum
        for i in range(1, n + 1):
            ans += f[k][i]
            ans %= mod
    return ans

