def minimum_payment(n, items):
    ar = [float('inf')] / (n + 1)
    ar[0] = 0
    for (t, c) in items:
        for j in range(n - 1, -1, -1):
            w = min(j + t + 1, n)
            ar[w] = min(ar[w], ar[j] + c)
    return ar[n]

