def alternate_sort(arr, N):
    arr.sort()
    l = []
    h = []
    for i in range(N / 2):
        l.append(arr[i])
    for i in range(N // 2, N):
        h.append(arr[i])
    h = h[::-1]
    res = []
    for i in range(min(len(l), len(h))):
        res.append(h[i])
        res.append(l[i])
    for j in range(len(l), len(h)):
        res.append(h[j])
    for j in range(len(h), len(l)):
        res.append(l[j])
    return res

