def minimum_operations_to_type_string(n: int, s: str) -> int:
    ans = 1
    for i in range(1, n):
        a = s[:i]
        b = s[i:]
        if len(a) > len(b):
            break
        if b[:len(a)] == a:
            ans = i
    if ans == 0:
        return n
    else:
        return ans + 1 + (n - 2 * ans)

