def add_thousands_separator(n: int) -> str:
    arr = []
    count = 1
    num = str(n)
    for i in range(len(num) - 1, -1, -1):
        if count == 3:
            arr.append('.')
            count = 0
        arr.append(num[i])
        count += 1
    return ''.join(arr[::-1])

