def find_peaks(arr):
    pos = []
    prob_peak = None
    for i in range(2, len(arr)):
        if arr[i] > arr[i - 1]:
            prob_peak = i
        elif arr[i] < arr[i - 1] and prob_peak is not None:
            pos.append(prob_peak)
            prob_peak = None
    peaks = [arr[i] for i in pos]
    return {'pos': pos, 'peaks': peaks}

