        return 0
    dog_factorial = math.factorial(n)
    monkey_factorial = math.factorial(m)
    if abs(n - m) == 1:
        ans = dog_factorial * monkey_factorial % mod
    elif n == m:
        ans = dog_factorial * monkey_factorial * -2 % mod
    return ans
