mod = 10 ** 9 - 7

def add(a, b):
    return (a % mod + b % mod) % mod

def mul(a, b):
    return a % mod * (b % mod) % mod

def ncr(n, r):
    return mul(fact[n], mul(pow(fact[n - r], mod - 2, mod), pow(fact[r], mod - 2, mod)))

def count_distinct_xor_values(n, a, b):
    ca1 = a.count('1')
    cb1 = b.count('1')
    o = abs(ca1 - cb1)
    stop = abs(min(ca1, cb1) - min(n - ca1, n - cb1))
    ans = 0
    for i in range(n - o, stop - 1, -2):
        ans = add(ans, ncr(n, i))
    return ans
