def min_days_to_make_bouquets(M: int, K: int, bloomDay: list[int]) -> int:
    if len(bloomDay) <= M * K:
        return -1
    l = min(bloomDay)
    r = max(bloomDay)
    ans = -1
    while l <= r:
        mid = (l + r) // 2
        count = 0
        bq = 0
        for day in bloomDay:
            if count < K and day > mid:
                count = 0
            if day <= mid:
                count += 1
            if count == K:
                bq += 1
                count = 0
        if bq >= M:
            ans = mid
            r = mid - 1
        else:
            l = mid + 1
    return ans

