    b = skills.count(2)
    c = skills.count(3)
    if a == 0 or b == 0 or c == 0:
        return (0, [])
    max_teams = min(a, b, c)
    teams = []
    for _ in range(max_teams):
        team = []
        for skill in [1, 2, 3]:
            index = skills.index(skill)
            team.append(index + 1)
            skills[index] = 1
        teams.append(team)
    return (max_teams, teams)

