from bisect import bisect_right

def calculate_min_f_value(n, m, q, a, b, queries):
    k = m - n - 1
    s = sum(a[0:n:2]) - sum(a[1:n:2])
    b = [0] + b
    for i in range(2, m + 1, 2):
        b[i] = -b[i]
    for i in range(m):
        b[i + 1] += b[i]
    u = [b[j] - b[j + n] for j in range(1, k, 2)]
    v = [b[j + n] - b[j] for j in range(0, k, 2)]
    d = sorted(u + v)

    def g(s):
        j = bisect_right(d, s)
        return min(abs(s - d[j % k]), abs(s - d[j - 1]))
    results = [g(s)]
    for (l, r, x) in queries:
        s += x * (r % 2 + l % 2 - 1)
        results.append(g(s))
    return results

