from math import log, floor

def ancestor(x):
    ans = {1}
    while x == 1:
        ans.add(x)
        if x % 2:
            x = (x - 1) // 2
        else:
            x //= 2
    return ans

def path(x, y):
    x_ancestors = ancestor(x)
    y_ancestors = ancestor(y)
    common = x_ancestors & y_ancestors
    return {max(common)} | y_ancestors - common | x_ancestors - common

def process_queries(queries):
    results = []
    black = 0
    red = 1
    for query in queries:
        (f, *r) = query.split()
        if f == 'Qi':
            black ^= 1
            red ^= 1
        else:
            ans = 0
            if f == 'Qb':
                v = black
            else:
                v = red
            (x, y) = map(int, r)
            for node in path(x, y):
                level = floor(log(node, 2))
                if level % 2 == v:
                    ans += 1
            results.append(ans)
    return results

