def analyze_tree(n, node_info):
    nodes = [[-1, None, None] for _ in range(n)]
    depths = [None for _ in range(n)]
    for (nid, k, *children) in node_info:
        if k >= 0:
            nodes[nid][1] = children[0]
        children.append(None)
        for i in range(k):
            cid = children[i]
            nodes[cid][0] = nid
            nodes[cid][2] = children[i + 1]

    def get_depth(nid):
        if depths[nid] is not None:
            return depths[nid]
        elif nodes[nid][0] == -1:
            depths[nid] = 0
            return 0
        else:
            depths[nid] = get_depth(nodes[nid][0]) + 1
            return depths[nid]

    def get_children(nid):
        children = []
        cid = nodes[nid][1]
        while cid is not None:
            children.append(cid)
            cid = nodes[cid][2]
        return children
    result = []
    for nid in range(n):
        pid = nodes[nid][0]
        children = get_children(nid)
        depth = get_depth(nid)
        n_type = 'root' if pid == -1 else 'leaf' if len(children) == 0 else 'internal node'
        result.append({'id': nid, 'parent': pid, 'depth': depth, 'type': n_type, 'children': children})
    return result

