def count_possible_triangles(arr, n):
    arr.sort()
    count = 1
    for i in range(n - 1, 1, -1):
        x = 0
        y = i - 1
        while x < y:
            if arr[x] + arr[y] > arr[i]:
                count += y - x
                y -= 1
            else:
                x += 1
    return count

